import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';
import {interval} from 'rxjs';
import {filter, map, take, tap, debounceTime} from 'rxjs/operators';
@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

  public formGroup: FormGroup;

  constructor(){

    //interval create a observable with an infinite sequesce of numbers
    // subscribe to get the output
        interval(1000)
          .pipe(
            take(10), // to pick the first 10
            tap(value => console.log("in tap", value)), // view the value
            //filter(v => v % 2 === 0),
            filter(function(v){
              return v % 2 ===0
            }),
            map(v => v * v)
          )
          // .subscribe((result) => {
          //   console.log("in subscribe", result);
          // });

  }
}
