import { Component } from "@angular/core";

@Component({
    selector: "data-binding",
    templateUrl: "./databinding.component.html"
})
export class DataBindingComponent{

    name: string;
    count: number;

    constructor(){
        this.name = "Anil Joseph";
        this.count = 10;
    }

    inc(){
        this.count++;
    }
}
