import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';

import { AppComponent } from './app.component';
import {HelloComponent} from './hello/hello.component';
import { DataBindingComponent } from './databinding/databinding.component';
import {FormsModule} from '@angular/forms';
import { ProductsModule } from './products/products.module';
import {RouterModule, Routes} from '@angular/router';
import { ViewNotFoundComponent } from './view-not-found/view-not-found.component';
import { GadgetsModule } from './gadgets/gadgets.module';
import { LoginComponent } from './login/login.component';

//Define the route-view(component) mapping
const routes: Routes = [

  {path: "home", component: HelloComponent},
  {path: "databinding", component: DataBindingComponent},
  {path: "", redirectTo: "/home", pathMatch: "full"},
  {path: "**", component: ViewNotFoundComponent},
]


@NgModule({
  declarations: [
    AppComponent, HelloComponent, DataBindingComponent, ViewNotFoundComponent, LoginComponent
  ],
  imports: [
    BrowserModule, FormsModule, ProductsModule, RouterModule.forRoot(routes), GadgetsModule
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
