import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import {Product} from '../../model/Product';

@Component({
  selector: 'app-list-products',
  templateUrl: './list-products.component.html',
  styleUrls: ['./list-products.component.css']
})
export class ListProductsComponent {

  public data: Array<Product> = [];
  public nProduct: Product = new Product();
  public searchKey: string = "";
  private url: string;


  constructor(private httpClient: HttpClient){

      this.url = "http://localhost:9000/products";
      httpClient.get<Array<Product>>(this.url)
                    .subscribe({
                      next: (data) => { console.log("next", data); this.data = data},
                      error: (error) => {console.log("error", error)},
                      complete: ()=> {console.log("completed")}
                    });

  }

  saveProduct(){

    this.httpClient.post(this.url, this.nProduct)
                      .subscribe({
                        next: () => {
                            alert("Product saved");
                            this.data.push(this.nProduct);
                            this.nProduct = new Product();
                        },
                        error: () => {
                            alert("Failed to save Product")
                        }

                      })


  }

  deleteProduct(product: Product){

    const url = this.url + "/" + product.id;
    this.httpClient.delete(url)
                      .subscribe({
                        next: () => {
                          alert("deleted product " + product.id);
                          const index = this.data.findIndex(item => item.id === product.id);
                          this.data.splice(index, 1);
                        },
                        error: () => {
                          alert("Failed to delete the product");
                        }
                      })
  }
}
<h4>List Products</h4>

<div>
    <input type="search" placeholder="Search Products" [(ngModel)]="searchKey"/>
</div>
<p *ngIf="searchKey">
    Search results for {{searchKey}}
</p>
<br/>
<table>
    <thead>
        <tr>
            <th>Index</th>
            <th>Product ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Description</th>
        </tr>
    </thead>
    <tbody>
        <!-- iterate over data and create tr for each product -->
        <tr *ngFor="let item of data | productFilter : searchKey; index as i; even as even; odd as odd" 
                                    [ngClass]="{oddCls: odd, evenCls: even}">
            <td>{{i}}</td>
            <td>{{item.id}}</td>
            <td>{{item.name | uppercase}}</td>
            <td>{{item.price | currency : 'RM'}}</td>
            <td>{{item.description}}</td>
            <td>
                <button class="btn btn-danger" (click)="deleteProduct(item)">Delete</button>
                &nbsp;
                <button class="btn btn-warning" (click)="editProduct(item)">Edit</button>
            </td>
        </tr>
        <tr>
            <td>
                <button (click)="saveProduct()">Save</button>
            </td>
            <td>
                <input type="number" placeholder="ID" [(ngModel)]="nProduct.id"/>
            </td>
            <td>
                <input placeholder="Name" [(ngModel)]="nProduct.name"/>
            </td>
            <td>
                <input type="number" placeholder="Price" [(ngModel)]="nProduct.price"/>
            </td>
            <td>
                <input placeholder="Description" [(ngModel)]="nProduct.description"/>
            </td>
        </tr>
    </tbody>
</table>
