import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from 'src/app/model/Product';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent {

  public productId: number = 0
  public product: Product = new Product()

  constructor(private activatedRoute: ActivatedRoute, private httpClient: HttpClient){

    this.productId = activatedRoute.snapshot.params["id"];


    const url = "http://localhost:9000/products/" + this.productId;
    httpClient.get(url)
                .subscribe({
                  next: (data) => {
                    this.product = data;
                  },
                  error: () => {
                    alert("Cannot read record..")
                  }
                })

  }

}
<h4>Edit Product: {{productId}}</h4>

<form>
        <div class="form-group">
            <label>Name</label>
            <input class="form-control" placeholder="Name" [(ngModel)]="product.name"/>
        </div>

        <div class="form-group">
            <label>Price</label>
            <input class="form-control" placeholder="Price" [(ngModel)]="product.price"/>
        </div>

        <div class="form-group">
            <label>Description</label>
            <input class="form-control" placeholder="Description" [(ngModel)]="product.description"/>
        </div>
        <br/>
        <div>
            <button class="btn btn-success">Save</button>&nbsp;
            <button class="btn btn-info">Cancel</button>
        </div>
</form>
