import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import {interval} from 'rxjs';
import {filter, map, take, tap, debounceTime} from 'rxjs/operators';
import {HttpClient, HttpParams} from '@angular/common/http';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

  public formGroup: FormGroup;

  constructor(private httpClient: HttpClient){

    //interval create a observable with an infinite sequesce of numbers
    // subscribe to get the output
        interval(1000)
          .pipe(
            take(10), // to pick the first 10
            tap(value => console.log("in tap", value)), // view the value
            //filter(v => v % 2 === 0),
            filter(function(v){
              return v % 2 ===0
            }),
            map(v => v * v)
          )
          // .subscribe((result) => {
          //   console.log("in subscribe", result);
          // });


     this.formGroup = new FormGroup({
        search: new FormControl("", [], [])
     });    

     this.formGroup
            .get("search")
            ?.valueChanges
            .pipe(
              debounceTime(1000)
            )
            .subscribe(input => {
              //console.log("input", input);

              const url = "https://en.wikipedia.org/w/api.php";
              const params = new HttpParams()
                                    .set("action", "opensearch")
                                    .set("limit", 20)
                                    .set("origin", "*")
                                    .set("search", input);

              // httpClient
              //     .get(url, {params, observe: 'response'})
              //     .subscribe((data) => {
              //       console.log(data);
              // })

              httpClient
              .get(url, {params, observe: 'body'})
              .subscribe((data) => {
                console.log(data);
          })



            })

  }
}
<h4>Wiki Search</h4>

<div [formGroup]="formGroup">
    <input type="search" placeholder="Wiki Search" formControlName="search"/>
</div>
