import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from 'src/app/model/Product';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent {

  public productId: number = 0
  public product: Product = new Product()

  constructor(private activatedRoute: ActivatedRoute, private httpClient: HttpClient){

    this.productId = activatedRoute.snapshot.params["id"];


    const url = "http://localhost:9000/products/" + this.productId;
    httpClient.get(url)
                .subscribe({
                  next: (data) => {
                    this.product = data;
                  },
                  error: () => {
                    alert("Cannot read record..")
                  }
                })

  }

}
<h4>Edit Product: {{productId}}</h4>

<!-- FormGroup -->
<form #form #formGroup="ngForm">
        {{form}}
        {{formGroup}}
        <div class="form-group">
            <label>Name</label>
            <!-- FormControl( provided we are using ngModel) -->
            <input name="productName" 
                    class="form-control" 
                    placeholder="Name" [(ngModel)]="product.name" required/>
            <div>The field is required</div>
        </div>

        <div class="form-group">
            <label>Price</label>
            <!-- FormControl -->
            <input name="productPrice" class="form-control" placeholder="Price" [(ngModel)]="product.price"/>
        </div>

        <div class="form-group">
            <label>Description</label>
            <!-- FormControl -->
            <input name="productDesc" class="form-control" placeholder="Description" [(ngModel)]="product.description"/>
        </div>
        <br/>
        <div>
            <button class="btn btn-success" [disabled]="formGroup.pristine || formGroup.invalid">Save</button>&nbsp;
            <button class="btn btn-info">Cancel</button>
        </div>
</form>
